 p_id=>wwv_flow_api.id(707676137361278871)
,p_name=>'WayBillReportQuery'
,p_query_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select TMSWAYBILL_PKG.fullname(wb.waybilldate, wb.vehicleid, wb.personid) as WayBillFullName,',
'       wb.waybillid,',
'       wb.waybilldate,',
'       wb.waybillnotes,',
'       tr.tripqueue,',
'       tr.tripdistance,',
'       tr.tripname,',
'       tr.tripnotes',
'  from twaybilllog wb',
'  join ttripslog tr on (wb.waybillid = tr.waybillid)',
'where wb.waybillid = :P39_WAYBILLID',
' order by wb.waybilldate, 1, tr.tripqueue'))
,p_report_layout_id=>wwv_flow_api.id(710084493316533158)
,p_format=>'PDF'
